import { useLayoutEffect, useRef, useState } from "react";

export const useMeasure = (ref, deps) => {
  const [rect, setRect] = useState({});
  const divRef = useRef();

  useLayoutEffect(() => {
    setRect(divRef.current.getBoundingClientRect());
  }, deps);

  return rect;
};
